//?seccion de imports
import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav.jsx';
import { useState } from 'react';
import axios from 'axios';

import { Routes, Route } from "react-router-dom";
import About from './views/About.jsx';
import Detail from './views/Deatil.jsx';

import Form from './components/Form/Form.jsx';
import { useLocation } from "react-router-dom";

//?seccion estilos
import style from './App.module.css'



function App() {
  
  //?llamado al estado
  const [characters, setCharacters] = useState([]);
  const [userData, setUserData] = useState({
    email: '',
    password: '',
  })

  //?llamado a la ubicacion
  const location = useLocation();

  //! funcion para buscar en la api de rick and morty, el personaje que queramos
  const onSearch = (id) => {
    axios(`https://rickandmortyapi.com/api/character/${id}`)
    .then(response => response.data)
    .then((data) => {
      if (data.name) {
        setCharacters((oldChars) => [...oldChars, data]);
      } else {
        window.alert('¡No hay personajes con este ID!');
      }
    })
    .catch(() => {
      window.alert('No hay personajes con ese ID')
    });
  }


  //! funcion para cerrar la card que ya no queremos
  const onClose = (id) => {
    const characterFiltered = characters.filter(character => character.id !== Number(id))
    setCharacters(characterFiltered)
  }

  //! funcion que nos permite reflejar el texto ingresado a nuestro estado local
  const handleChange = (event) => {
    const property = event.target.name;
    const value = event.target.value;

    setUserData({...userData, [property]: value});
  }
  
  return (
    <div className = {style.principalContainer}>

      {/* Seccion location */}
        {
          // Seccion de Nav 
          location.pathname !== '/' ? <Nav onSearch = {onSearch} /> : null
        }

      <Routes >
        {/* Seccion de form*/}
        <Route path='/' element={<Form userData={userData}/>} />

        {/* Seccion de cards */}
        <Route path='/home' element={<Cards characters={characters} onClose = {onClose}/>}/>
        
        {/* Seccion de about     */}
        <Route path='/about' element={<About/>}/>
        
        {/* Seccion de detailCards */}
        <Route path='/detail/:id' element={<Detail/>}/>


          {/* <footer className = {style.footer}>
            <p>Hecho y Realizado por Gulliano Jaimes @ 2023 SoyHenry</p>
          </footer> */}
      </Routes>

    </div>
  );
}

export default App;
