import { ADD_FAV, REMOVE_FAV } from "./actions"


let initialState = {myFavorites: []}

const reducer = (state = initialState, action) => {

  switch(action.type){
    
    case ADD_FAV:
      return {...state, myFavorite: [...state.myFavorite, action.payload]}
    
    case REMOVE_FAV:
      return {...state, myFavorite: state.myFavorite.filter(fav => fav.id !== action.payload)}
    
    
    default:
      return{...state}
  }
}

export default reducer;
