import style from "./Card.module.css"

import { Link } from "react-router-dom";
import { connect } from "react-redux"
import { addFav, removeFav } from "../../redux/actions"
import { useState, useEffect } from "react";

const Card = ({id, name, species, gender, status, origin, image, onClose, addFav, removeFav, myFavorites}) => {

  const [isFav, setIsFav] = useState(false);


  const handleFavorite = (character) => {
    if(isFav){
      setIsFav(false);
      removeFav(id);
    } else {
      setIsFav(true);
      addFav(id, name, species, gender, status, origin, image)
    }
  }

  useEffect(() => {
    myFavorites.forEach((fav) => {
       if (fav.id === id) {
          setIsFav(true);
       }
    });
 }, [myFavorites]);

  return (
    <div className ={style.containerCard} >
          {
            isFav ? (
                <button onClick={handleFavorite}>‚ù§Ô∏è</button>
            ) : (
                <button onClick={handleFavorite}>ü§ç</button>
            )
          }

      <div className = {style.card} >
        <button onClick={() => onClose(id)}>X</button>
        <img src={image} alt={name} />
        
        <Link to={`/detail/${id}`} className={style.link}>
          <h3>{name}</h3>
        </Link>

        <div className = {style.info}>
          <p>Estatus: {status}</p>
          <p>Especie: {species}</p>
          <p>Genero: {gender}</p>
          <p>Origen: {origin}</p>
        </div>
      </div>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    myFavorites: state.myFavorites
  }
}

const mapDispatchToProps = (dispatch) => {
  return{
    addFavorite: (character) => dispatch(addFav(character)),
    removeFavorite: (id) => dispatch(removeFav(id)),
  }

}

export default connect(
  mapStateToProps,
  mapDispatchToProps,
)(Card);
