const validation = (userData) => {

  const errors = {};

  //errores email
  if(!userData.email) errors.email = 'Email vacio';
  else{

    if(/^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{3})+$/.test(userData.email)) return errors.email = 'Email invalido';
    else{

      if(userData.email.lenght > 35) errors.email = 'El email debe ser menor a 35 caracteres';
    };
  };


  //errores password
  if(!/.*\d+/.test(userData.password)) errors.password = 'La contrase単a debe tener un numero';
  else {

    if(userData.password.lenght < 6 || userData.password.lenght > 10) errors.password = 'La contrase単a debe tener entre 6 y 10 caracteres'
  }


  return errors;
} 

export default validation;
